#!/usr/bin/env python3
"""Make links for BinderHubs."""
import re
import urllib.parse
from pathlib import Path
from typing import Sequence

repo = "tueda/PS2020SS"


def make_link(string: str, branch: str, notebook: str) -> str:
    """Return the string with BinderHub links for the notebook."""
    filename = urllib.parse.quote(notebook)

    binder_alt = "Binder"
    # binder_lab_alt = "Binder Lab"
    colab_alt = "Open In Colab"

    binder_image = "https://mybinder.org/badge_logo.svg"
    # binder_image = "https://img.shields.io/badge/launch-binder-579ACA.svg?logo=data:image/png;base64,"  # noqa: E501
    # binder_lab_image = "https://img.shields.io/badge/launch-binder%20lab-E66581.svg?logo=data:image/png;base64,"  # noqa: E501
    colab_image = "https://colab.research.google.com/assets/colab-badge.svg"

    binder_url = f"https://mybinder.org/v2/gh/{repo}/{branch}?filepath={filename}"
    # binder_lab_url = (
    #     f"https://mybinder.org/v2/gh/{repo}/{branch}?urlpath=lab/tree/{filename}"
    # )
    colab_url = (
        f"https://colab.research.google.com/github/{repo}/blob/{branch}/{filename}"
    )
    github_url = f"https://github.com/{repo}/blob/{branch}/{filename}"

    return (
        f"[{string}]({github_url})"
        + f" [![{binder_alt}]({binder_image})]({binder_url})"
        + f" [![{colab_alt}]({colab_image})]({colab_url})"
    )


def make_contents(
    input_lines: Sequence[str], branch: str, notebook_dir: str
) -> Sequence[str]:
    """Make contents."""
    notebooks = sorted(
        str(f) for f in Path(notebook_dir).iterdir() if f.suffix.lower() == ".ipynb"
    )
    links = {re.sub(r"^.*_|\.ipynb$", "", f): f for f in notebooks}

    output_lines = []
    for line in input_lines:
        m = re.match(r"^(\d+\.|-)\s+\[(\S+)\]", line)
        if not m:
            m = re.match(r"^(\d+\.|-)\s+(\S+)", line)
        if m:
            prelude = m.group(1)
            title = m.group(2)
            if title in links:
                line = f"{prelude} {make_link(title, branch, links[title])}"
        output_lines.append(line)
    return output_lines


def process_file(filename: str, branch: str, notebook_dir: str) -> None:
    """Process the specified file."""
    path = Path(filename)
    input_lines = path.read_text().splitlines()
    output_lines = make_contents(input_lines, branch, notebook_dir)
    if input_lines != output_lines:
        print(f"patch {path}")
        path.write_text("\n".join(output_lines) + "\n")


def main() -> None:
    """Entry point."""
    process_file("notebooks/index.md", "develop", "notebooks")
    process_file("docs/index.md", "gh-pages", "docs/notebooks")


if __name__ == "__main__":
    main()
